import "../Styles/Filtering.css"; // or your preferred path
import { useState } from "react";
function Filtering() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleFilters = () => setIsOpen(!isOpen);

  return (
    <div className="filter-wrapper">
      <div className={`filter-bar-container ${isOpen ? "expanded" : ""}`}>
        <div className="filter-bar">
            <div className="filter-section">
                <h2>price</h2>
                <label htmlFor="pricemax">Max.</label>
                <input type="number" name="pricemax" id="pricemax" />
                <label htmlFor="pricemin">Min.</label>
                <input type="number" name="pricemin" id="pricemin" />
            </div>
            <div className="filter-section">
                <h2>popularity</h2>
                <label htmlFor="popmax">Max.</label>
                <input type="number" name="popmax" id="popmax" />
                <label htmlFor="popmin">Min.</label>
                <input type="number" name="popmin" id="popmin" />
            </div>
        </div>
      </div>
      <button className="filter-toggle-button" onClick={toggleFilters}>
        {isOpen ? "Hide Filters" : "Show Filters"}
      </button>
    </div>
  );
}

export default Filtering;
