import { useState, useRef } from "react";
import "../Styles/Filtering.css";

function Filtering() {
  const [filters, setFilters] = useState({
    pricemin: "",
    pricemax: "",
    popmin: "",
    popmax: "",
  });
  const filterRef = useRef(null);

  const isModified =
    parseFloat(filters.pricemin || 0) > 0 ||
    parseFloat(filters.pricemax || 0) > 0 ||
    parseFloat(filters.popmin || 0) > 0 ||
    parseFloat(filters.popmax || 0) > 0;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Apply filters with:", filters);
    // Call API or trigger data update here
  };

  return (
    <div className="filter-wrapper">
      <form onSubmit={handleSubmit}>
        <button
          type={isModified ? "submit" : "button"}
          className="filter-toggle-button"
          onClick={() => {
            if (!isModified) {
              filterRef.current?.scrollIntoView({
                behavior: "smooth",
                block: "start",
              });
            }
          }}
        >
          {isModified ? "Apply Filters" : "Show Filters"}
        </button>

        <div
          ref={filterRef}
          className={`filter-bar-container ${!isModified ? "expanded" : ""}`}
        >
          <div className="filter-bar">
            <div className="filter-section">
              <h2>Price</h2>
              <div className="field-pair">
                <label htmlFor="pricemin">Min</label>
                <div className="dollar-input-wrapper">
                  <span className="dollar-prefix">$</span>
                  <input
                    type="number"
                    id="pricemin"
                    name="pricemin"
                    min="0"
                    step="0.01"
                    value={filters.pricemin}
                    onChange={handleChange}
                  />
                </div>
              </div>
              <div className="field-pair">
                <label htmlFor="pricemax">Max</label>
                <div className="dollar-input-wrapper">
                  <span className="dollar-prefix">$</span>
                  <input
                    type="number"
                    id="pricemax"
                    name="pricemax"
                    min="0"
                    step="0.01"
                    value={filters.pricemax}
                    onChange={handleChange}
                  />
                </div>
              </div>
            </div>

            <div className="filter-section">
              <h2>Popularity</h2>
              <div className="field-pair">
                <label htmlFor="popmin">Min</label>
                <input
                  type="number"
                  id="popmin"
                  name="popmin"
                  min="0"
                  step="1"
                  placeholder="0"
                  value={filters.popmin}
                  onChange={handleChange}
                />
              </div>
              <div className="field-pair">
                <label htmlFor="popmax">Max</label>
                <input
                  type="number"
                  id="popmax"
                  name="popmax"
                  min="0"
                  step="1"
                  placeholder="100"
                  value={filters.popmax}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
}

export default Filtering;
