import { useState, useRef } from "react";
import "../Styles/Filtering.css";

function Filtering() {
  const [isOpen, setIsOpen] = useState(false);
  const [filters, setFilters] = useState({
    pricemin: "",
    pricemax: "",
    popmin: "",
    popmax: "",
  });

  const filterRef = useRef(null);

  const toggleFilters = () => {
    setIsOpen(prev => {
      const newState = !prev;
      if (!prev) {
        setTimeout(() => {
          filterRef.current?.scrollIntoView({
            behavior: "smooth",
            block: "start",
          });
        }, 100);
      }
      return newState;
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Applying filters:", filters);
    // You can trigger your API here
  };

  return (
    <div className="filter-wrapper">
      <button className="filter-toggle-button" onClick={toggleFilters}>
        {isOpen ? "Hide Filters" : "Show Filters"}
      </button>

      <div
        ref={filterRef}
        className={`filter-bar-container ${isOpen ? "expanded" : ""}`}
      >
        <form onSubmit={handleSubmit}>
          <div className="filter-bar">
            <div className="filter-section">
              <h2>Price</h2>
              <div className="field-pair">
                <label htmlFor="pricemin">Min</label>
                <input
                  type="number"
                  id="pricemin"
                  name="pricemin"
                  min="0"
                  step="0.01"
                  placeholder="0.00"
                  value={filters.pricemin}
                  onChange={handleChange}
                />
              </div>
              <div className="field-pair">
                <label htmlFor="pricemax">Max</label>
                <input
                  type="number"
                  id="pricemax"
                  name="pricemax"
                  min="0"
                  step="0.01"
                  placeholder="0.00"
                  value={filters.pricemax}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div className="filter-section">
              <h2>Popularity</h2>
              <div className="field-pair">
                <label htmlFor="popmin">Min</label>
                <input
                  type="number"
                  id="popmin"
                  name="popmin"
                  min="0"
                  step="1"
                  placeholder="0"
                  value={filters.popmin}
                  onChange={handleChange}
                />
              </div>
              <div className="field-pair">
                <label htmlFor="popmax">Max</label>
                <input
                  type="number"
                  id="popmax"
                  name="popmax"
                  min="0"
                  step="1"
                  placeholder="100"
                  value={filters.popmax}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>

          <div style={{ textAlign: "center", marginTop: "1rem" }}>
            <button type="submit" className="filter-apply-button">
              Apply Filters
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Filtering;
