import "../Styles/Card.css"
import Stars from "./Stars";
import { useState } from "react";

const [selectedGold, setSelectedGold] = useState('');

  const handleChange = (e) => {
    setSelectedGold(e.target.value);
  };

function Card() {
    return(
        <>
                <div class="product-card">
                    <div class="product-image">
                        <img src="https://placehold.co/300x300?text=Lamba" alt="Modern Masa Lambası"/>
                    </div>
                    <div class="product-info">
                        <h3>Modern Masa Lambası</h3>
                        <p>749.99 TL</p>
                    </div>
                    <form>
                    <div className="radio-wrapper">
                        <input
                        type="radio"
                        id="YellowGold"
                        name="gold"
                        value="Yellow Gold"
                        onChange={handleChange}
                        />
                        {selectedGold === 'Yellow Gold' && (
                        <label htmlFor="YellowGold">Yellow Gold</label>
                        )}
                    </div>

                    <div className="radio-wrapper">
                        <input
                        type="radio"
                        id="WhiteGold"
                        name="gold"
                        value="White Gold"
                        onChange={handleChange}
                        />
                        {selectedGold === 'White Gold' && (
                        <label htmlFor="WhiteGold">White Gold</label>
                        )}
                    </div>

                    <div className="radio-wrapper">
                        <input
                        type="radio"
                        id="RoseGold"
                        name="gold"
                        value="Rose Gold"
                        onChange={handleChange}
                        />
                        {selectedGold === 'Rose Gold' && (
                        <label htmlFor="RoseGold">Rose Gold</label>
                        )}
                    </div>
                    </form>
                    <Stars/>
                </div>
        </>
    );
}
export default Card;
