import { Rating } from "react-simple-star-rating";
import "../Styles/Stars.css";

export default function Stars({
  popularityScore = 0, // expects a number like 0.92
  color = "#FACC6B",
  iconSize = 14,
}) {
  // Convert 0.0–1.0 -> 0.0–5.0 and round to 1 decimal
  const ratingOutOfFive = Math.round((popularityScore * 5 + Number.EPSILON) * 10) / 10;

  // Just for debugging (remove in production)
  console.log("Raw popularityScore:", popularityScore);
  console.log("Converted to 5-star scale:", ratingOutOfFive);

  return (
    <div className="starsContainer">
      <Rating
        initialValue={ratingOutOfFive}
        readonly
        allowFraction
        size={iconSize}
        fillColor={color}
        emptyColor="grey"
        SVGstyle={{ display: "inline-block" }}
        style={{ marginRight: "5px" }}
      />
      <p>{ratingOutOfFive.toFixed(1)} / 5</p>
    </div>
  );
}
