import { useState, useRef } from "react";
import "../Styles/Filtering.css";

function Filtering() {
  const [isOpen, setIsOpen] = useState(false);
  const filterRef = useRef(null);

  const toggleFilters = () => {
    setIsOpen(prev => {
      const newState = !prev;
      if (!prev) {
        setTimeout(() => {
          filterRef.current?.scrollIntoView({
            behavior: "smooth",
            block: "center",
          });
        }, 100); // Wait a bit for expand animation
      }
      return newState;
    });
  };

  return (
    <div className="filter-wrapper">
      <button className="filter-toggle-button" onClick={toggleFilters}>
        {isOpen ? "Hide Filters" : "Show Filters"}
      </button>

      <div
        ref={filterRef}
        className={`filter-bar-container ${isOpen ? "expanded" : ""}`}
      >
        <div className="filter-bar">
          <div className="filter-section">
            <h2>Price</h2>
            <div className="field-pair">
              <label htmlFor="pricemin">Min</label>
              <input
                type="number"
                id="pricemin"
                name="pricemin"
                min="0"
                placeholder="0.00$"
              />
            </div>
            <div className="field-pair">
              <label htmlFor="pricemax">Max</label>
              <input
                type="number"
                id="pricemax"
                name="pricemax"
                min="0"
                placeholder="0.00$"
              />
            </div>
          </div>

          <div className="filter-section">
            <h2>Popularity</h2>
            <div className="field-pair">
              <label htmlFor="popmin">Min</label>
              <input
                type="number"
                id="popmin"
                name="popmin"
                min="0"
                placeholder="0"
              />
            </div>
            <div className="field-pair">
              <label htmlFor="popmax">Max</label>
              <input
                type="number"
                id="popmax"
                name="popmax"
                min="0"
                placeholder="100"
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Filtering;
