import { useState, useEffect } from "react";
import "../Styles/Filtering.css";

function FilteringBar({ onApply }) {
  const [isOpen, setIsOpen] = useState(false);
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(100);
  const [minPop, setMinPop] = useState(0);
  const [maxPop, setMaxPop] = useState(100);
  const [sortOrder, setSortOrder] = useState(null); // "asc" | "desc"

  const togglePanel = () => setIsOpen(!isOpen);

  useEffect(() => {
    // Apply filters whenever they change
    onApply({
      minPrice,
      maxPrice,
      minPopularity: minPop,
      maxPopularity: maxPop,
      sort: sortOrder,
    });
  }, [minPrice, maxPrice, minPop, maxPop, sortOrder]);

  return (
    <div className="filter-wrapper">
      <button className="filter-toggle-button" onClick={togglePanel}>
        {isOpen ? "Hide Filters" : "Show Filters"}
      </button>

      <div className={`filter-bar-container ${isOpen ? "expanded" : ""}`}>
        <div className="filter-bar">
          <div className="filter-group">
            <label>Price Range: {minPrice} - {maxPrice}</label>
            <div className="range-slider">
              <input
                type="range"
                min="0"
                max="1000"
                value={minPrice}
                onChange={(e) =>
                  setMinPrice(Math.min(Number(e.target.value), maxPrice - 1))
                }
              />
              <input
                type="range"
                min="0"
                max="1000"
                value={maxPrice}
                onChange={(e) =>
                  setMaxPrice(Math.max(Number(e.target.value), minPrice + 1))
                }
              />
            </div>
          </div>

          <div className="filter-group">
            <label>Popularity: {minPop} - {maxPop}</label>
            <div className="range-slider">
              <input
                type="range"
                min="0"
                max="100"
                value={minPop}
                onChange={(e) =>
                  setMinPop(Math.min(Number(e.target.value), maxPop - 1))
                }
              />
              <input
                type="range"
                min="0"
                max="100"
                value={maxPop}
                onChange={(e) =>
                  setMaxPop(Math.max(Number(e.target.value), minPop + 1))
                }
              />
            </div>
          </div>

          <div className="sort-group">
            <label>Sort by:</label>
            <div className="sort-buttons">
              <button
                className={sortOrder === "asc" ? "active" : ""}
                onClick={() => setSortOrder("asc")}
              >
                Price ↑
              </button>
              <button
                className={sortOrder === "desc" ? "active" : ""}
                onClick={() => setSortOrder("desc")}
              >
                Price ↓
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FilteringBar;
