import "../Styles/Filtering.css"; // or your preferred path
import { useState } from "react";
function Filtering() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleFilters = () => setIsOpen(!isOpen);

  return (
    <div className="filter-wrapper">
      <button className="filter-toggle-button" onClick={toggleFilters}>
        {isOpen ? "Hide Filters" : "Show Filters"}
      </button>

      <div className={`filter-bar-container ${isOpen ? "expanded" : ""}`}>
        <div className="filter-bar">
          <div className="filter-item">price ascending</div>

          <div className="filter-center">
            <div className="filter-label">filters</div>
            <div className="slider-wrapper">
              <input type="range" min="0" max="100" className="slider" />
              <div className="slider-label">popularity</div>
            </div>
          </div>

          <div className="filter-item">price descending</div>
        </div>
      </div>
    </div>
  );
}

export default Filtering;
