import { useState, useEffect } from "react";
import "../Styles/Filtering.css";

function Filtering({ onFilterChange }) {
  const [isOpen, setIsOpen] = useState(false);

  // Range states
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(100);
  const [minPop, setMinPop] = useState(0);
  const [maxPop, setMaxPop] = useState(100);
  const [sort, setSort] = useState(null); // "asc" or "desc"

  const toggleFilters = () => setIsOpen(!isOpen);

  // Trigger API update when filters change
  useEffect(() => {
    onFilterChange({
      minPrice,
      maxPrice,
      minPopularity: minPop,
      maxPopularity: maxPop,
      sort,
    });
  }, [minPrice, maxPrice, minPop, maxPop, sort]);

  return (
    <div className="filter-wrapper">
      <div className={`filter-bar-container ${isOpen ? "expanded" : ""}`}>
        <div className="filter-bar">
          <button
            className={`filter-item ${sort === "asc" ? "active" : ""}`}
            onClick={() => setSort("asc")}
          >
            price ascending
          </button>

          <div className="filter-center">
            <div className="slider-wrapper">
              <label className="slider-label">popularity</label>
              <input
                type="range"
                min="0"
                max="100"
                value={minPop}
                onChange={(e) =>
                  setMinPop(Math.min(+e.target.value, maxPop - 1))
                }
                className="slider thumb-left"
              />
              <input
                type="range"
                min="0"
                max="100"
                value={maxPop}
                onChange={(e) =>
                  setMaxPop(Math.max(+e.target.value, minPop + 1))
                }
                className="slider thumb-right"
              />
            </div>
            <div className="slider-values">
              {minPop} - {maxPop}
            </div>
          </div>

          <button
            className={`filter-item ${sort === "desc" ? "active" : ""}`}
            onClick={() => setSort("desc")}
          >
            price descending
          </button>
        </div>
        <div className="price-range">
          <label>Price range: {minPrice} - {maxPrice}</label>
          <input
            type="range"
            min="0"
            max="100"
            value={minPrice}
            onChange={(e) =>
              setMinPrice(Math.min(+e.target.value, maxPrice - 1))
            }
          />
          <input
            type="range"
            min="0"
            max="100"
            value={maxPrice}
            onChange={(e) =>
              setMaxPrice(Math.max(+e.target.value, minPrice + 1))
            }
          />
        </div>
      </div>

      <button className="filter-toggle-button" onClick={toggleFilters}>
        {isOpen ? "Hide Filters" : "Show Filters"}
      </button>
    </div>
  );
}

export default Filtering;
