import "../Styles/Card.css"
import Stars from "./Stars";
import { useState } from "react";

const [selectedGold, setSelectedGold] = useState('');

  const handleChange = (e) => {
    setSelectedGold(e.target.value);
  };

function Card() {
    return(
        <>
                <div class="product-card">
                    <div class="product-image">
                        <img src="https://placehold.co/300x300?text=Lamba" alt="Modern Masa Lambası"/>
                    </div>
                    <div class="product-info">
                        <h3>Modern Masa Lambası</h3>
                        <p>749.99 TL</p>
                    </div>
                    <form action="POST">
                        <div class="radio-wrapper">
                            <input type="radio" name="gold" id="YellowGold" />
                            <label for="YellowGold">Yellow Gold</label>
                        </div>
                        <div class="radio-wrapper">
                            <input type="radio" name="gold" id="WhiteGold" />
                            <label for="WhiteGold">White Gold</label>
                        </div>
                        <div class="radio-wrapper">
                            <input type="radio" name="gold" id="RoseGold" />
                            <label for="RoseGold">Rose Gold</label>
                        </div>
                    </form>
                    <Stars/>
                </div>
        </>
    );
}
export default Card;
