import { useState } from "react";

function Filtering() {
    const [dropdownOpen, setDropdownOpen] = useState(false);

    const toggleDropdown = () => {
        setDropdownOpen(!dropdownOpen);
    };

    return (
        <div className="panel" style={{ backgroundColor: "#D9D9D9", color: "white", padding: "1rem" }}>
            <div className="panel-item">
                <input type="range" name="pricerange" id="pricerange" />
                <label htmlFor="pricerange">
                    <i className="fa-solid fa-tags"></i>
                </label>
            </div>

            <div className="panel-item">
                <button onClick={toggleDropdown} type="button" style={{ background: "transparent", border: "none", color: "white", cursor: "pointer" }}>
                    Filter by Popularity{" "}
                    <i className={`fa-solid ${dropdownOpen ? "fa-chevron-up" : "fa-chevron-down"}`}></i>
                </button>

                {dropdownOpen && (
                    <div className="dropdown" style={{ marginTop: "0.5rem" }}>
                        <button type="button" style={{ display: "block", marginBottom: "0.5rem" }}>Popularity increasing</button>
                        <button type="button" style={{ display: "block" }}>Popularity decreasing</button>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Filtering;
